(5, '{\"title\":\"B\\u00e0i 1: SQL b\\u1eaft \\u0111\\u1ea7u\",\"count\":\"5\",\"data\":[{\"q\":\"SQL c\\u00f3 th\\u1ec3 d\\u00f9ng c\\u00e1c h\\u1ed7 tr\\u1ee3 sau \\u0111\\u01b0\\u1ee3c kh\\u00f4ng\",\"a1\":\"Mysql\",\"a2\":\"SQL Server\",\"a3\":\"SQL\",\"a4\":\"T\\u1ea5t c\\u1ea3 c\\u00e1c \\u00fd tr\\u00ean\",\"c\":\"a4\"},{\"q\":\"SQL l\\u00e0 g\\u00ec ?\",\"a1\":\"SQL l\\u00e0 vi\\u1ebft t\\u1eaft c\\u1ee7a t\\u1eeb Structured Query Language, ngh\\u0129a l\\u00e0 ng\\u00f4n ng\\u1eef truy v\\u1ea5n c\\u01a1 s\\u1edf d\\u1eef li\\u1ec7u\",\"a2\":\"SQL l\\u00e0 t\\u00ean g\\u1ecdi c\\u1ee7a m\\u00e1y t\\u00ednh\",\"a3\":\"SQL l\\u00e0 t\\u00ean thu\\u1eadt to\\u00e1n\",\"a4\":\"Kh\\u00f4ng c\\u00f3 \\u00fd ngh\\u00eda g\\u00ec\",\"c\":\"a1\"},{\"q\":\"SQL xu\\u1ea5t hi\\u1ec7n n\\u0103m n\\u00e0o\",\"a1\":\"1970\",\"a2\":\"1971\",\"a3\":\"1973\",\"a4\":\"1974\",\"c\":\"a4\"},{\"q\":\"C\\u00e1c \\u0111\\u1ed1i t\\u01b0\\u1ee3ng hay d\\u00f9ng CSDL\",\"a1\":\"Qu\\u1ea3n tr\\u1ecb CSDL\",\"a2\":\"Ng\\u01b0\\u1eddi d\\u00f9ng cu\\u1ed1i\",\"a3\":\"Chuy\\u00ean vi\\u00ean tin h\\u1ecdc\",\"a4\":\"T\\u1ea5t c\\u1ea3 \\u0111\\u00e1p \\u00e1n\",\"c\":\"a4\"},{\"q\":\"Phi\\u00ean l\\u00e0m vi\\u1ec7c l\\u00e0 g\\u00ec\",\"a1\":\"L\\u00e0 m\\u1ed9t phi\\u00ean ch\\u1ee3\",\"a2\":\"Phi\\u00ean l\\u00e0m vi\\u1ec7c hay c\\u00f2n g\\u1ecdi l\\u00e0 session, \\u0111\\u00e2y l\\u00e0 m\\u1ed9t kho\\u1ea3ng th\\u01a1i gian \\u0111\\u01b0\\u1ee3c gi\\u1edbi h\\u1ea1n b\\u1edfi th\\u1eddi gian b\\u1eaft \\u0111\\u1ea7u (time start) v\\u00e0 th\\u1eddi gian k\\u1ebft th\\u00fac (time end)\",\"a3\":\"Phi\\u00ean l\\u00e0m vi\\u1ec7c l\\u00e0 th\\u1eddi gian b\\u1eaft \\u0111\\u1ea7u\",\"a4\":\"Kh\\u00f4ng c\\u00f3 \\u0111\\u00e1p \\u00e1n \\u0111\\u00fang\",\"c\":\"a2\"}]}', '2023-11-25 05:42:54', '2023-11-25 05:42:54'),
(6, '{\"title\":\"B\\u00e0i 2: C\\u00e1c ki\\u1ec3u d\\u1eef li\\u1ec7u truy v\\u1ea5n\",\"count\":\"5\",\"data\":[{\"q\":\"SQL c\\u00f3 ngh\\u0129a l\\u00e0 g\\u00ec?\",\"a1\":\"Strong Question Language\",\"a2\":\"Structured Query Language\",\"a3\":\"Structured QuestionLanguage\",\"a4\":\"Kh\\u00f4ng c\\u00f3 \\u0111\\u00e1p \\u00e1n \\u0111\\u00fang\",\"c\":\"a3\"},{\"q\":\"C\\u00e2u l\\u1ec7nh SQL n\\u00e0o \\u0111\\u01b0\\u1ee3c s\\u1eed d\\u1ee5ng \\u0111\\u1ec3 tr\\u00edch xu\\u1ea5t d\\u1eef li\\u1ec7u t\\u1eeb c\\u01a1 s\\u1edf d\\u1eef li\\u1ec7u?\",\"a1\":\"OPEN\",\"a2\":\"SELECT\",\"a3\":\"FROM\",\"a4\":\"GET\",\"c\":\"a2\"},{\"q\":\"C\\u00e2u l\\u1ec7nh SQL n\\u00e0o \\u0111\\u01b0\\u1ee3c s\\u1eed d\\u1ee5ng \\u0111\\u1ec3 c\\u1eadp nh\\u1eadt d\\u1eef li\\u1ec7u trong c\\u01a1 s\\u1edf d\\u1eef li\\u1ec7u?\",\"a1\":\"SAVE\",\"a2\":\"UPDATE\",\"a3\":\"MODIFY\",\"a4\":\"SAVE AS\",\"c\":\"a2\"},{\"q\":\"C\\u00e2u l\\u1ec7nh SQL n\\u00e0o \\u0111\\u01b0\\u1ee3c s\\u1eed d\\u1ee5ng \\u0111\\u1ec3 x\\u00f3a d\\u1eef li\\u1ec7u kh\\u1ecfi c\\u01a1 s\\u1edf d\\u1eef li\\u1ec7u?\",\"a1\":\"DELETE\",\"a2\":\"REMOVE\",\"a3\":\"UPDATE\",\"a4\":\"COLLAPSE\",\"c\":\"a1\"},{\"q\":\"C\\u00e2u l\\u1ec7nh SQL n\\u00e0o \\u0111\\u01b0\\u1ee3c s\\u1eed d\\u1ee5ng \\u0111\\u1ec3 ch\\u00e8n d\\u1eef li\\u1ec7u m\\u1edbi v\\u00e0o c\\u01a1 s\\u1edf d\\u1eef li\\u1ec7u?\",\"a1\":\"ADD RECORD\",\"a2\":\"INSERT NEW\",\"a3\":\"INSERT INTO\",\"a4\":\"ADD NEW\",\"c\":\"a3\"}]}', '2023-11-24 14:37:23', '2023-11-24 14:37:23'),
(7, '{\"title\":\"B\\u00e0i 3: Ki\\u1ec3m tra truy v\\u1ea5n\",\"count\":\"5\",\"data\":[{\"q\":\"V\\u1edbi SQL, l\\u00e0m c\\u00e1ch n\\u00e0o \\u0111\\u1ec3 ch\\u1ecdn c\\u1ed9t c\\u00f3 t\\u00ean \\\"FirstName\\\" t\\u1eeb b\\u1ea3ng c\\u00f3 t\\u00ean \\\"Persons\\\"?\",\"a1\":\"SELECT FirstName FORM Persons\",\"a2\":\"EXTRACT FirstName FORM Persons\",\"a3\":\"SELECT Persons FORM FirstName\",\"a4\":\"SELECT Persons,FirstName\",\"c\":\"a1\"},{\"q\":\"V\\u1edbi SQL, l\\u00e0m c\\u00e1ch n\\u00e0o \\u0111\\u1ec3 ch\\u1ecdn t\\u1ea5t c\\u1ea3 c\\u00e1c c\\u1ed9t t\\u1eeb b\\u1ea3ng c\\u00f3 t\\u00ean \\\"Ng\\u01b0\\u1eddi\\\"?\",\"a1\":\"SELECT all FORM Persons\",\"a2\":\"SELECT * FORM Persons\",\"a3\":\"SELECT Persons\",\"a4\":\"SELECT *  Persons\",\"c\":\"a2\"},{\"q\":\"V\\u1edbi SQL, l\\u00e0m c\\u00e1ch n\\u00e0o \\u0111\\u1ec3 ch\\u1ecdn t\\u1ea5t c\\u1ea3 c\\u00e1c b\\u1ea3n ghi t\\u1eeb b\\u1ea3ng c\\u00f3 t\\u00ean \\\"Ng\\u01b0\\u1eddi\\\" trong \\u0111\\u00f3 gi\\u00e1 tr\\u1ecb c\\u1ee7a c\\u1ed9t \\\"T\\u00ean\\\" l\\u00e0 \\\"Peter\\\"?\",\"a1\":\"SELECT * FORM Persons WHERE FirstName = \\\"Peter\\\"\",\"a2\":\"SELECT * FORM Persons AS FirstName = \\\"Peter\\\"\",\"a3\":\"SELECT all FORM Persons WHERE FirstName = \\\"Peter\\\"\",\"a4\":\"OPEN all FORM Persons WHERE FirstName = \\\"Peter\\\"\",\"c\":\"a1\"},{\"q\":\"V\\u1edbi SQL, l\\u00e0m c\\u00e1ch n\\u00e0o \\u0111\\u1ec3 b\\u1ea1n ch\\u1ecdn t\\u1ea5t c\\u1ea3 c\\u00e1c b\\u1ea3n ghi t\\u1eeb b\\u1ea3ng c\\u00f3 t\\u00ean \\\"Ng\\u01b0\\u1eddi\\\" trong \\u0111\\u00f3 gi\\u00e1 tr\\u1ecb c\\u1ee7a c\\u1ed9t \\\"T\\u00ean\\\" b\\u1eaft \\u0111\\u1ea7u b\\u1eb1ng \\\"a\\\"?\",\"a1\":\"SELECT * FORM Persons WHERE FirstName LIKE \\\"%a\\\"\",\"a2\":\"SELECT * FORM Persons WHERE FirstName = \\\"a%\\\"\",\"a3\":\"SELECT * FORM Persons WHERE FirstName = \\\"%a\\\"\",\"a4\":\"SELECT * FORM Persons WHERE FirstName = \\\"%a%\\\"\",\"c\":\"a2\"},{\"q\":\"V\\u1edbi SQL, l\\u00e0m c\\u00e1ch n\\u00e0o \\u0111\\u1ec3 b\\u1ea1n ch\\u1ecdn t\\u1ea5t c\\u1ea3 c\\u00e1c b\\u1ea3n ghi t\\u1eeb m\\u1ed9t b\\u1ea3ng c\\u00f3 t\\u00ean \\\"Ng\\u01b0\\u1eddi\\\" trong \\u0111\\u00f3 \\\"T\\u00ean\\\" l\\u00e0 \\\"Peter\\\" v\\u00e0 \\\"H\\u1ecd\\\" l\\u00e0 \\\"Jackson\\\"?\",\"a1\":\"SELECT FirstName=\'Peter\', LastName=\'Jackson\' FROM Persons\",\"a2\":\"SELECT * FROM Persons WHERE FirstName<>\'Peter\' AND LastName<>\'Jackson\'\",\"a3\":\"SELECT * FROM Persons WHERE FirstName=\'Peter\' AND LastName=\'Jackson\'\",\"a4\":\"kh\\u00f4ng c\\u00f3 \\u0111\\u00e1p \\u00e1n\",\"c\":\"a2\"}]}', '2023-11-24 14:46:14', '2023-11-24 14:46:14'),
(9, '{\"title\":\"B\\u00e0i 4: QUIZ 1\",\"count\":\"5\",\"data\":[{\"q\":\"Which SQL statement is used to extract data from a database?\",\"a1\":\"DELETE\",\"a2\":\"REMOVE\",\"a3\":\"COLLAPSE\",\"a4\":\"C\\u1ea3 ba \\u0111\\u1ec1u\",\"c\":\"a1\"},{\"q\":\"Which SQL statement is used to insert new data in a database?\",\"a1\":\"ADD RECORD\",\"a2\":\"INSERT NEW\",\"a3\":\"INSERT INTO\",\"a4\":\"ADD NEW\",\"c\":\"a3\"},{\"q\":\"With SQL, how do you select a column named \\\"FirstName\\\" from a table named \\\"Persons\\\"?\",\"a1\":\"SELECT Persons FROM FirstName\",\"a2\":\"SELECT PersonsFROM Persons\",\"a3\":\"SELECT FirstName FROM FirstName\",\"a4\":\"SELECT FirstName FROM Persons\",\"c\":\"a4\"},{\"q\":\"With SQL, how do you select all the columns from a table named \\\"Persons\\\"?\",\"a1\":\"SELECT FROM Persons\",\"a2\":\"SELECT * FROM Persons\",\"a3\":\"SELECT ALL FROM Persons\",\"a4\":\"SELECT *. Persons\",\"c\":\"a2\"},{\"q\":\"With SQL, how do you select all the records from a table named \\\"Persons\\\" where the value of the column \\\"FirstName\\\" starts with an \\\"a\\\"?\",\"a1\":\"SELECT * FROM Persons WHERE FirstName LIKE \'a%\'\",\"a2\":\"SELECT  FROM Persons WHERE FirstName LIKE \'a%\'\",\"a3\":\"SELECT * Persons WHERE FirstName = \'a\'\",\"a4\":\"SELECT * FROM Persons WHERE FirstName = \\\"Persons\\\"\",\"c\":\"a1\"}]}', '2023-12-17 18:20:47', '2023-12-17 18:20:47'),
(10, '{\"title\":\"B\\u00e0i 5: QUIZ 2\",\"count\":\"5\",\"data\":[{\"q\":\"The OR operator displays a record if ANY conditions listed are true. The AND operator displays a record if ALL of the conditions listed are true\",\"a1\":\"True\",\"a2\":\"False\",\"a3\":\"null\",\"a4\":null,\"c\":\"a1\"},{\"q\":\"With SQL, how do you select all the records from a table named \\\"Persons\\\" where the \\\"LastName\\\" is alphabetically between (and including) \\\"Hansen\\\" and \\\"Pettersen\\\"?\",\"a1\":\"SELECT LastName BETWEEN \'Hansen\' AND \'Pettersen\'\",\"a2\":\"SELECT * FROM Persons WHERE LastName BETWEEN <Hansen> AND <Pettersen>\",\"a3\":\"SELECT * FROM Persons WHERE LastName BETWEEN \'Hansen\' AND \'Pettersen\'\",\"a4\":\"SELECT * FROM Persons * LastName BETWEEN \'Hansen\' AND \'Pettersen\'\",\"c\":\"a3\"},{\"q\":\"Which SQL statement is used to return only different values?\",\"a1\":\"SELECT DIFFENRENT\",\"a2\":\"SELECT DISTINCT\",\"a3\":\"DISTINCT\",\"a4\":\"SELECT UNIQUE\",\"c\":\"a2\"},{\"q\":\"Which SQL keyword is used to sort the result-set?\",\"a1\":\"SORT BY\",\"a2\":\"ORDER\",\"a3\":\"ORDER BY\",\"a4\":\"SORT\",\"c\":\"a3\"},{\"q\":\"With SQL, how can you return all the records from a table named \\\"Persons\\\" sorted descending by \\\"FirstName\\\"?\",\"a1\":\"SELECT * FROM Persons ORDER FirstName DESC\",\"a2\":\"SELECT * FROM Persons ORDER BY FirstName SORT\",\"a3\":\"SELECT * FROM Persons SORTBY FirstName DESC\",\"a4\":\"SELECT * FROM Persons ORDER BY FirstName DESC\",\"c\":\"a4\"}]}', '2023-12-17 18:27:48', '2023-12-17 18:27:48'),
(13, '{\"title\":\"B\\u00e0i 6; QUIZ 3\",\"count\":\"5\",\"data\":[{\"q\":\"Which SQL statement is used to create a database table called \'Customers\'?\",\"a1\":\"CREATE DATABASE TAB Customers\",\"a2\":\"CREATE TABLE Customers\",\"a3\":\"CREATE DATABASE TABLE Customers\",\"a4\":\"CREATE DB Customers\",\"c\":\"a2\"},{\"q\":\"How can you change \\\"Hansen\\\" into \\\"Nilsen\\\" in the \\\"LastName\\\" column in the Persons table?\",\"a1\":\"UPDATE Persons SET LastName=\'Hansen\' INTO LastName=\'Nilsen\'\",\"a2\":\"MODIFY Persons SET LastName=\'Hansen\' INTO LastName=\'Nilsen\",\"a3\":\"MODIFY Persons SET LastName=\'Nilsen\' WHERE LastName=\'Hansen\'\",\"a4\":\"UPDATE Persons SET LastName=\'Nilsen\' WHERE LastName=\'Hansen\'\",\"c\":\"a4\"},{\"q\":\"With SQL, how can you insert a new record into the \\\"Persons\\\" table?\",\"a1\":\"INSERT (\'Jimmy\', \'Jackson\') INTO Persons\",\"a2\":\"INSERT INTO Persons VALUES (\'Jimmy\', \'Jackson\')\",\"a3\":\"INSERT VALUES (\'Jimmy\', \'Jackson\') INTO Persons\",\"a4\":\"INSERT VALUES ( \'Jackson\') INTO Persons\",\"c\":\"a2\"},{\"q\":\"With SQL, how can you return all the records from a table named \\\"Persons\\\" sorted descending by \\\"FirstName\\\"?\",\"a1\":\"SELECT * FROM Persons ORDER FirstName DESC\",\"a2\":\"SELECT * FROM Persons ORDER BY FirstName DESC\",\"a3\":\"SELECT * FROM Persons SORT BY \'FirstName\' DESC\",\"a4\":\"SELECT * FROM Persons SORT BY \'FirstName\' DESC\",\"c\":\"a2\"},{\"q\":\"What is the most common type of join?\",\"a1\":\"INSIDE JOIN\",\"a2\":\"JOINED TABLE\",\"a3\":\"INNER JOIN\",\"a4\":\"JOINED\",\"c\":\"a3\"}]}', '2023-12-23 07:02:36', '2023-12-23 07:02:36');

